document.addEventListener("DOMContentLoaded",(function(){const e=document.getElementById("hamburger-toggle"),t=document.getElementsByClassName("overlay")[0];e.addEventListener("click",()=>{e.classList.contains("hamburger--active")&&t.classList.contains("overlay--open")?(e.classList.remove("hamburger--active"),t.classList.remove("overlay--open"),document.body.classList.remove("scroll-disabled")):(e.classList.add("hamburger--active"),t.classList.add("overlay--open"),document.body.classList.add("scroll-disabled"))}),document.querySelectorAll(".switch__item").forEach((e,t)=>{e.addEventListener("click",()=>{document.querySelectorAll(".switch__item").forEach(e=>e.classList.remove("switch__item--active")),document.querySelectorAll(".switch__content").forEach(e=>e.classList.remove("switch__content--active")),e.classList.add("switch__item--active"),document.querySelectorAll(".switch__content")[t].classList.add("switch__content--active")})}),document.querySelectorAll(".tabs__item").forEach((e,t)=>{e.addEventListener("click",()=>{document.querySelectorAll(".tabs__item").forEach(e=>e.classList.remove("tabs__item--active")),document.querySelectorAll(".tabs__content").forEach(e=>e.classList.remove("tabs__content--active")),e.classList.add("tabs__item--active"),document.querySelectorAll(".tabs__content")[t].classList.add("tabs__content--active")})})}));